export function selectSort(array) {
  const animations = [];
  let minIndex = 0;
  for (let i = 0; i < array.length; i++) {
    let min = 0;
    for (let j = i + 1; j < array.length; j++) {
      if (array[j] < min) {
        min = array[j];
        minIndex = j;
      }
    }
    array[minIndex] = array[i];
    array[i] = min;
    animations.push([i, minIndex]);
    animations.push([i, minIndex]);
    // animations.push([array[i], array[minIndex]]);
    //  animations.push(minIndex, array[i]);
  }
  return animations;
}
