export function selectSort(array) {
  const animations = [];
  let minIndex = 0;
  for (let i = 0; i < array.length; i++) {
    let min = 99999999;
    for (let j = i + 1; j < array.length; j++) {
      if (array[j] < min) {
        min = array[j];
        minIndex = j;
      }
    }
    if (min == 99999999) continue;
    array[minIndex] = array[i];
    array[i] = min;
    animations.push([i, minIndex]);
    animations.push([i, minIndex]);
    animations.push([i, min]);
    // animations.push([array[i], array[minIndex]]);
    //  animations.push(minIndex, array[i]);
  }
  console.log('select sort ', array);
  return animations;
}

function selectionSort(items) {
  var len = items.length,
    min;

  for (let i = 0; i < len; i++) {
    //set minimum to this position
    min = i;

    //check the rest of the array to see if anything is smaller
    for (j = i + 1; j < len; j++) {
      if (items[j] < items[min]) {
        min = j;
      }
    }

    //if the minimum isn't in the position, swap it
    if (i != min) {
      swap(items, i, min);
    }
  }

  return items;
}
